$(function(){$("#auction-form").validate({rules:{title:{required:!0},description:{required:!0}},errorPlacement:function(e,r){var s=$(r).attr("name");$(".error-message").show(),$(".error-message").append(" <li> Please enter a "+s+" for your auction </li>")},success:function(e){$("#success").css("display","block")}})}),$(function(){$("#register-form").validate({rules:{name:{required:!0},email:{required:!0}},errorPlacement:function(e,r){var s=$(r).attr("name");$(".error-message").show(),$(".error-message").append(" <li> Please fill in an existing "+s+" </li>")},success:function(e){$("#success").css("display","block")}})});